import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth, useAuthNavigation } from '../hooks/useAuth';

const SideBar = () => {
  const location = useLocation();
  const { user, isAdmin } = useAuth();
  const { logoutAndNavigate } = useAuthNavigation();

  return (
    <nav className="sidebar">
      {/* ... остальной код ... */}

      {user ? (
        <button onClick={logoutAndNavigate} className="logout-button">
          Выйти
        </button>
      ) : (
        <Link to="/login">
          <button className={location.pathname === '/login' ? 'active' : ''}>
            Вход
          </button>
        </Link>
      )}
    </nav>
  );
};
