import React, { useState, useEffect } from 'react';
import { usePageTracking } from '../../hooks/usePageTracking';

const PostsPanel = () => {
  usePageTracking(5); // ID для страницы "Посты"
  const [posts, setPosts] = useState([]);
  const [postCount, setPostCount] = useState(5);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      try {
        const response = await fetch('http://localhost:8000/posts');
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  const displayedPosts = posts.slice(0, postCount);

  if (loading) {
    return <div className="loading">Загрузка постов...</div>;
  }

  return (
    <div className="posts-panel">
      <h1>Посты</h1>

      <div className="controls">
        <div className="slider-container">
          <label htmlFor="post-slider">
            Количество постов: {postCount}
          </label>
          <input
            id="post-slider"
            type="range"
            min="1"
            max="10"
            value={postCount}
            onChange={(e) => setPostCount(parseInt(e.target.value))}
            className="post-slider"
          />
        </div>
      </div>

      <div className="post-list">
        <h3>Отображается {displayedPosts.length} из {posts.length} постов</h3>
        {displayedPosts.map(post => (
          <div key={post.id} className="post-item">
            <h3 className="post-title">{post.title}</h3>
            <p className="post-body">{post.body}</p>
            <div className="post-meta">
              <span>Post ID: {post.id}</span>
              <span>User ID: {post.userId}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PostsPanel;
