import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { usePageTracking } from '../../hooks/usePageTracking';

const PostsPanel = () => {
  usePageTracking(10); // ID для страницы "Посты"

  const [posts, setPosts] = useState([]);
  const [count, setCount] = useState(5);
  const [loading, setLoading] = useState(true);
  const [mode, setMode] = useState("fetch");
  const [error, setError] = useState(null);

  // === Загрузка данных через fetch (с твоего локального сервера) ===
  const loadFetch = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch("http://localhost:8000/posts");
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setPosts(data);
    } catch (err) {
      console.error('Error fetching posts (fetch):', err);
      setError('Не удалось загрузить посты с локального сервера');
      // Fallback на внешний API если локальный не работает
      try {
        const fallback = await fetch("https://jsonplaceholder.typicode.com/posts");
        const data = await fallback.json();
        setPosts(data);
        setError(null);
      } catch (fallbackErr) {
        console.error('Fallback also failed:', fallbackErr);
      }
    } finally {
      setLoading(false);
    }
  };

  // === Загрузка данных через axios (с твоего локального сервера) ===
  const loadAxios = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get("http://localhost:8000/posts");
      setPosts(response.data);
    } catch (err) {
      console.error('Error fetching posts (axios):', err);
      setError('Не удалось загрузить посты с локального сервера');
      // Fallback на внешний API
      try {
        const fallback = await axios.get("https://jsonplaceholder.typicode.com/posts");
        setPosts(fallback.data);
        setError(null);
      } catch (fallbackErr) {
        console.error('Fallback also failed:', fallbackErr);
      }
    } finally {
      setLoading(false);
    }
  };

  // === Переключение между методами ===
  useEffect(() => {
    if (mode === "fetch") loadFetch();
    if (mode === "axios") loadAxios();
  }, [mode]);

    // === Мемоизация: ограничиваем количество постов ===
    const slicedPosts = useMemo(() => {
      return Array.isArray(posts) ? posts.slice(0, count) : [];
    }, [posts, count]);

    if (error && slicedPosts.length === 0) {
      return (
        <main className="content">
        <header>
        <h1>Посты</h1>
        <p className="lead">Загрузка данных из сети</p>
        </header>
        <div className="error-message" style={{
          background: '#fef2f2',
          padding: '1rem',
          borderRadius: '8px',
          color: '#dc2626'
        }}>
        <p>⚠️ {error}</p>
        <p>Попробуйте:</p>
        <ul style={{ marginLeft: '1.5rem' }}>
        <li>Проверить авторизацию (страница /me должна работать)</li>
        <li>Убедиться что сервер запущен на localhost:8000</li>
        <li>Использовать кнопки ниже для переключения режима</li>
        </ul>
        </div>

        <div style={{ marginTop: '1rem' }}>
        <button onClick={() => setMode("fetch")} className="control" style={{ marginRight: 10 }}>
        fetch
        </button>
        <button onClick={() => setMode("axios")} className="control">
        axios
        </button>
        </div>
        </main>
      );
    }

    return (
      <main className="content">
      <header>
      <h1>Посты</h1>
      <p className="lead">Загрузка данных из сети. Выберите количество отображаемых постов.</p>

      {/* Источник данных */}
      <div style={{
        marginBottom: "1rem",
        padding: "0.5rem",
        background: "#f1f5f9",
        borderRadius: "6px",
        fontSize: "0.875rem"
      }}>
      <strong>Источник:</strong> {mode === "fetch" ? "Fetch API" : "Axios"} →
      {window.location.hostname === 'localhost' ? " localhost:8000" : " JSONPlaceholder"}
      </div>
      </header>

      {/* Переключатель режимов */}
      <div style={{ marginBottom: "1rem" }}>
      <button
      onClick={() => setMode("fetch")}
      className="control"
      style={{ marginRight: 10, background: mode === "fetch" ? "#ff7a59" : "" }}
      >
      fetch
      </button>
      <button
      onClick={() => setMode("axios")}
      className="control"
      style={{ background: mode === "axios" ? "#ff7a59" : "" }}
      >
      axios
      </button>
      </div>

      {/* Ползунок */}
      <div style={{ marginBottom: "1.5rem" }}>
      <label>
      Количество постов: <strong>{count}</strong>
      <input
      type="range"
      min="1"
      max="20"
      value={count}
      onChange={e => setCount(Number(e.target.value))}
      style={{ width: "100%", marginTop: 8 }}
      />
      </label>
      </div>

      {error && (
        <div style={{
          background: '#fef3c7',
          padding: '0.75rem',
          borderRadius: '6px',
          marginBottom: '1rem',
          border: '1px solid #fbbf24'
        }}>
        ⚠️ Используется fallback API: {error}
        </div>
      )}

      <hr style={{ margin: "1.5rem 0" }} />

      {loading ? (
        <div style={{ textAlign: 'center', padding: '2rem' }}>
        <p>Загрузка постов...</p>
        <div style={{
          width: '40px',
          height: '40px',
          border: '3px solid #e2e8f0',
          borderTop: '3px solid #ff7a59',
          borderRadius: '50%',
          margin: '1rem auto',
          animation: 'spin 1s linear infinite'
        }}></div>
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          `}</style>
          </div>
      ) : (
        <>
        <p style={{ marginBottom: '1rem', color: '#64748b' }}>
        Отображается {slicedPosts.length} {slicedPosts.length === 1 ? 'пост' : 'постов'}
        </p>

        <ul style={{ display: "flex", flexDirection: "column", gap: "1.2rem" }}>
        {slicedPosts.map(post => (
          <li
          key={post.id}
          style={{
            background: "#fff",
            padding: "1rem",
            borderRadius: 12,
            boxShadow: "0 4px 12px rgba(0,0,0,0.06)",
                                  border: "1px solid #e2e8f0"
          }}
          >
          <h3 style={{
            marginBottom: ".5rem",
            color: "#0f172a"
          }}>
          {post.title}
          </h3>
          <p style={{
            color: "#475569",
            lineHeight: 1.6
          }}>
          {post.body}
          </p>
          <div style={{
            marginTop: "0.75rem",
            fontSize: "0.875rem",
            color: "#94a3b8",
            display: "flex",
            gap: "1rem"
          }}>
          <span>ID: {post.id}</span>
          <span>User: {post.userId}</span>
          </div>
          </li>
        ))}
        </ul>
        </>
      )}

      <footer style={{ marginTop: "2rem", paddingTop: "1rem", borderTop: "1px solid #e2e8f0" }}>
      <p style={{ color: "#64748b", fontSize: "0.875rem" }}>
      © {new Date().getFullYear()} — Работа с сетевыми запросами
      </p>
      </footer>
      </main>
    );
};

export default PostsPanel;
