import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { usePageTracking } from '../../hooks/usePageTracking';

export default function PostsPanel() {
  // Отслеживаем посещения этой страницы (ID=10)
  usePageTracking(10);

  const [count, setCount] = useState(5);       // количество отображаемых постов
  const [posts, setPosts] = useState([]);      // данные постов
  const [loading, setLoading] = useState(true);
  const [mode, setMode] = useState("fetch");   // fetch | axios

  // === Загрузка данных через fetch (JSONPlaceholder) ===
  const loadFetch = async () => {
    setLoading(true);
    try {
      const response = await fetch("https://jsonplaceholder.typicode.com/posts");
      const data = await response.json();
      setPosts(data);
    } catch (error) {
      console.error('Ошибка загрузки (fetch):', error);
    } finally {
      setLoading(false);
    }
  };

  // === Загрузка данных через axios (JSONPlaceholder) ===
  const loadAxios = async () => {
    setLoading(true);
    try {
      const response = await axios.get("https://jsonplaceholder.typicode.com/posts");
      setPosts(response.data);
    } catch (error) {
      console.error('Ошибка загрузки (axios):', error);
    } finally {
      setLoading(false);
    }
  };

  // === Переключение между методами ===
  useEffect(() => {
    if (mode === "fetch") loadFetch();
    if (mode === "axios") loadAxios();
  }, [mode]);

    // === Мемоизация: ограничиваем количество постов ===
    const slicedPosts = useMemo(() => {
      return posts.slice(0, count);
    }, [posts, count]);

    return (
      <main className="content">
      <header>
      <h1>Посты</h1>
      <p className="lead">Загрузка данных из сети. Выберите количество отображаемых постов.</p>
      </header>

      {/* Переключатель режимов */}
      <div style={{ marginBottom: "1rem" }}>
      <button
      onClick={() => setMode("fetch")}
      className="control"
      style={{ marginRight: 10 }}
      >
      fetch
      </button>
      <button onClick={() => setMode("axios")} className="control">
      axios
      </button>
      </div>

      {/* Ползунок */}
      <label>
      Количество постов: {count}
      <input
      type="range"
      min="1"
      max="20"
      value={count}
      onChange={e => setCount(Number(e.target.value))}
      style={{ width: "100%", marginTop: 8 }}
      />
      </label>

      <hr style={{ margin: "1.5rem 0" }} />

      {loading ? (
        <p>Загрузка...</p>
      ) : (
        <ul style={{ display: "flex", flexDirection: "column", gap: "1.2rem" }}>
        {slicedPosts.map(post => (
          <li
          key={post.id}
          style={{
            background: "#fff",
            padding: "1rem",
            borderRadius: 12,
            boxShadow: "0 4px 12px rgba(0,0,0,0.06)"
          }}
          >
          <h3 style={{ marginBottom: ".5rem" }}>{post.title}</h3>
          <p>{post.body}</p>
          </li>
        ))}
        </ul>
      )}

      <footer>
      <p>© 2025 — Работа с сетью</p>
      </footer>
      </main>
    );
}
